function renderIcon(string $key, string $class = "w-8 h-8"): string
{
  $lib = getIconLibrary();
  $icon = $lib[$key] ?? $lib['default'];

  if (strpos($icon, '<svg') !== false) {
    return "<div class='{$class}'>{$icon}</div>";
  }

  return "<div class='{$class} flex items-center justify-center text-2xl'>{$icon}</div>";
}
